group.script:
function main() {
    // Body
    if (params.width < 5)
        params.width = 5;

    if (params.height < 3)
        params.height = 3;

    // parts
    return cylinder({
        r: params.width / 2,
        h: params.height - 1
    });
}

function main() {
    // Fill
    return cylinder({
        r: (params.width / 2) - 1,
        h: 1
    }).translate([0, 0, params.height - 1]);
}

function main() {
    // Round Top
    return torus({
        ri: 1,
        ro: (params.width / 2) - 1,
        fni: 16
    }).translate([0, 0, params.height - 1]);
}

function main() {
    // Wick
    return cylinder({
        r: 0.2,
        h: params.height + 2
    });
}

function main() {
    // Flame Base
    var base;

    base = sphere({r: 1});

    // transformations
    base = base.scale([1, 1, 1.2]);
    base = base.translate([0, 0, params.height + 2]);

    return base;
}

function main() {
    // Flame Tip
    var tip;

    tip = cylinder({
        r1: 1.01,
        r2:0,
        h: 5.5
    });

    // transformations
    tip = tip.scale([1, 1, 1.2]);
    tip = tip.translate([0, 0, params.height + 2]);

    return tip;
}
