stack.script:
function main() {
    var tip;

    tip = cylinder({
        r1: 1.01,
        r2:0,
        h: 5.5
    });

    tip = tip.scale([1, 1, 1.2]);
    tip = tip.translate([0, 0, params.rise + 2]);

    return tip;
}

function main() {
    // Round Top
    return torus({
        ri: 1,
        ro: (params.width / 2) - 1,
        fni: 16
    }).translate([0, 0, params.height - 1]);
}
